import java.awt.*;import java.awt.event.ActionEvent;import java.awt.event.ActionListener;public class dialogFrame extends Dialog implements ActionListener{    TextField  upperBound, lowerBound, xValue;    Button buttonOK, buttonCancel;    Panel panelUpper, panelLower;    continuousDist bApplet;    double accumuProb = 0;    String titleOfButton;     public int indexError = 0;        int		formulaIndex;    continuousCanvas  cCanvas;    int 	index;			    public dialogFrame( Frame parent, continuousDist anApplet, int tmpIndex ){        super(parent, "Set Lower and Upper Limit", true);        bApplet = anApplet;        setLayout(new GridLayout(2,0,1,1));		index = tmpIndex;        this.setBackground(Color.lightGray);		        panelUpper = new Panel();        panelLower = new Panel();        buttonOK = new Button("   OK  ");        buttonOK.setSize(40,26);        buttonCancel = new Button("Cancel");        buttonCancel.setSize(40,26);				upperBound	= new TextField(4);		lowerBound	= new TextField(4);		xValue		= new TextField(4);        lowerBound.setSize(40,26);        upperBound.setSize(40,26);        panelUpper.setLayout(new FlowLayout());        panelLower.setLayout(new FlowLayout());        if  ( 	( index == 1 ) || ( index == 3 )  ){            panelUpper.add(new Label("Lower Limit ="));            panelUpper.add(lowerBound);        }        if  ( 	( index == 2 ) || ( index == 3 )  ){            panelUpper.add(new Label("Upper Limit ="));            panelUpper.add(upperBound);        }        if ( index == 4 ) {            panelUpper.add(new Label("x ="));            panelUpper.add(xValue);        }                add(panelUpper);        panelLower.add(buttonOK);        panelLower.add(buttonCancel);        buttonOK.addActionListener(this);        buttonCancel.addActionListener(this);        add(panelLower);                this.setSize(330, 120);        this.show();    }    public void actionPerformed(ActionEvent e) { 	  boolean emptyInputFlag = false;	  	  try {          if  ( e.getSource() == buttonOK ){                       double	uppBd = -2E8;            double	lowBd = -2E8;             			if  ( 	( index == 1 ) || ( index == 3 )  ){            	if ( new String(lowerBound.getText()).length() == 0 )            		emptyInputFlag = true; 				lowBd =	Double.valueOf(lowerBound.getText()).doubleValue();     		}        	if  ( 	( index == 2 ) || ( index == 3 )  ){            	if ( new String(upperBound.getText()).length() == 0 )            		emptyInputFlag = true; 				uppBd =	Double.valueOf(upperBound.getText()).doubleValue();			}        	if  ( index == 4 ){            	if ( new String(xValue.getText()).length() == 0 )            		emptyInputFlag = true; 				uppBd =	Double.valueOf(xValue.getText()).doubleValue(); 				lowBd = uppBd;			}			if (!emptyInputFlag){				bApplet.cInfo.changeInterval = true;           		bApplet.cInfo.setInterval(lowBd, uppBd, index);           		bApplet.cCanvas.repaint();           	}           	dispose();          }          if  ( e.getSource() == buttonCancel ){            dispose();          }      } catch (Exception ex) {dispose();}      	  bApplet.choiceProb.select("Prob");    }}