import java.awt.*;import java.awt.event.ActionEvent;import java.awt.event.ActionListener;public class incFrame extends Dialog implements ActionListener{    TextField  xValue, xInc;    Button buttonOK, buttonCancel;    Panel panelUpper, panelLower;    continuousDist bApplet;    int		index;    signCanvas sCanvas;    String titleOfButton, defaultString = new String();        public incFrame( Frame parent, continuousDist tmpApplet, int tmpIndex ){        super(parent, "Set Parameter and Increment", true);        this.setBackground(Color.gray);        bApplet = tmpApplet;        index = tmpIndex;            setLayout(new GridLayout(2,0,1,1));		String tmpString = new String("");		String defaultIncString = new String("");		        panelUpper = new Panel();        panelLower = new Panel();        panelUpper.setLayout(new FlowLayout());        panelLower.setLayout(new FlowLayout());       	buttonOK = new Button("   OK  ");        buttonOK.setSize(40,26);        buttonCancel = new Button("Cancel");        buttonCancel.setSize(40,26);				if ( tmpIndex == 99 ) {			defaultString = new Double(Math.round(bApplet.cInfo.quantile*100)							/100.0).toString();			defaultIncString = new Double(Math.round(bApplet.cInfo.quantileInc*100)							/100.0).toString();			tmpString = "quantile";			panelUpper.add(new Label(" q = "));		}		else {			sCanvas = new signCanvas(bApplet, tmpIndex);			sCanvas.setSize(12, 22);			panelUpper.add(sCanvas);			panelUpper.add(new Label(" = "));					defaultString =	new Double(Math.round((bApplet.cInfo.paraValue[tmpIndex])*100)							/100.0).toString();			defaultIncString = new Double(Math.round((bApplet.cInfo.paraIncre[tmpIndex])*100)							/100.0).toString();			tmpString ="";		}		defaultIncString = "";					xValue		= new TextField(defaultString, 4); 		xInc		= new TextField(defaultIncString, 4);				panelUpper.add(xValue);		panelUpper.add(new Label("Increment ="));		panelUpper.add(xInc);        add(panelUpper);		buttonOK.addActionListener(this);		buttonCancel.addActionListener(this);        panelLower.add(buttonOK);        panelLower.add(buttonCancel);        add(panelLower);                this.setSize(330, 120);        this.show();   }    public void actionPerformed(ActionEvent e) { 	  try {          if  ( e.getSource() == buttonOK ){                       int	uppBd = 0;            int	lowBd = 0;             						if  ( index == 99 ){ 				bApplet.cInfo.setQuantile(Double.valueOf(xValue.getText()).doubleValue());				bApplet.cInfo.setQuantileInc(Double.valueOf(xInc.getText()).doubleValue()); 			} 			else {	 				bApplet.cInfo.setValue(Double.valueOf(xValue.getText()).doubleValue(), index);				bApplet.cInfo.setInc(Double.valueOf(xInc.getText()).doubleValue(), index);     		}     					bApplet.cInfo.changeInterval = true;           	bApplet.cCanvas.repaint();           	bApplet.qCanvas.repaint();           	dispose();          }          if  ( e.getSource() == buttonCancel ){            dispose();          }      } catch (Exception ex) { dispose(); }	      }}