import java.awt.*;public class signCanvas extends Canvas {    Image			imageSign;    continuousDist	tmpApplet;	int				tmpIndex;    public signCanvas(continuousDist anApplet, int index) {				tmpApplet = anApplet;		tmpIndex = index;        }    public void paint (Graphics g) {        imageSign =	tmpApplet.getImage(tmpApplet.getCodeBase(),        			tmpApplet.cInfo.getImageFileName(tmpIndex));        g.drawImage(imageSign, 2, 2, 8, 12, this);        }}